    def submission(self):
        name = self.textEdit.toPlainText()
        cls = self.textEdit_2.toPlainText()
        gender = self.textEdit_3.toPlainText()
        roll_no = self.textEdit_4.toPlainText()
        school = self.textEdit_5.toPlainText()

        if (name == "") or (cls == "") or (gender == "") or (roll_no == "") or (school == ""):
            self.label_7.setText("Please fill all the fields .")

        elif self.radioButton.isChecked() == False:
            self.label_7.setText("Please tick the radio-button .")

        elif (name != "") and (cls != "") and (gender != "") and (roll_no != "") and (school != "") and self.radioButton.isChecked():
            with open("Data.txt", "a") as file1:
                file1.write(f"Name : {name}\nClass : {cls}\nGender : {gender}\nRoll Number : {roll_no}\nSchool Name : {school}\n")
            self.label_7.setText("Your form is submitted . Thank you for responding .")

