    def generator(self):
        pass_length = self.spinBox.value()

        if pass_length > 18:
            self.textBrowser_6.setText("Sorry , but you can't generate a password of more than 18 characters due to space issues .")

        else:
            self.textBrowser_6.setText("")
            def gen(i):
                listed_password = []

                alphabets = list(string.ascii_letters)
                numbers = list(string.digits)
                symbols = list(string.punctuation)

                characters = list(alphabets + numbers + symbols)

                random.shuffle(characters)

                for x in characters[:pass_length]:
                    listed_password.append(x)

                raw_password = ''.join(map(str, listed_password))

                i.setText(raw_password)

            gen(self.textBrowser_1)
            gen(self.textBrowser_2)
            gen(self.textBrowser_3)
            gen(self.textBrowser_4)

    def strengthner(self):
        passkey = self.lineEdit_1.text()
        security_list = [('i', '!'), ('and', '&'), ('a', '@'), ('s', '$'), ('c', '('), ('l', '1'), ('x', '*'), ('X', ')('), ('o', '0'), ('V', '\/'), ('H', '#'), ('I', '|')]

        for a, b in security_list:
            passkey = passkey.replace(a, b)

        self.textBrowser_5.setText(passkey)

    def clear1(self):
        self.lineEdit_2.clear()

    def clear2(self):
        self.lineEdit_3.clear()

    def clear3(self):
        self.lineEdit_4.clear()

    def clear_all(self):
        self.spinBox.clear()
        self.lineEdit_1.clear()
        self.lineEdit_2.clear()
        self.lineEdit_3.clear()
        self.lineEdit_4.clear()
        self.textBrowser_1.clear()
        self.textBrowser_2.clear()
        self.textBrowser_3.clear()
        self.textBrowser_4.clear()
        self.textBrowser_5.clear()
        self.textBrowser_6.clear()

    def confirm(self):
        username = self.lineEdit_2.text()
        password = self.lineEdit_3.text()
        purposes = self.lineEdit_4.text()

        with open("Passwords.txt", "a") as writer:
            information = f"Username : '{username}' , Password : '{password}' , Purposes : '{purposes}'"
            secure_data = ''.join(format(ord(i), '07b') for i in information)
            writer.write(secure_data)
            writer.write("\n")

